defaultValue.value:
{
  width: 1294.4,
  height: 800,
  margin: {
    top: 0,
    right: 0,
    bottom: 0,
    left: 0
  },
  grandparentText: {
    fontWeight: "bold"
  },
  rect: {
    fill: "none",
    stroke: "white"
  },
  childrenRect: {
    fill: "rgb(230,230,230)",
    strokeWidth: "1px",
    stroke: "white",
    fillOpacity: .5
  },
  parentRect: {
    fill: "none",
    stroke: "white",
    strokeWidth: "2px",
    fillOpacity: .5
  }
}

(config) => {
  const {
    cell,
    index,
    clickHandler,
    colorScale,
    hideLabelForValuesLessThan
  } = config;
  return (
    <g
      transform={"translate(" + cell.x + "," + cell.y + ")"}
      key={index}
      onClick={clickHandler}
      >
      <rect
        width={cell.dx}
        height={cell.dy}
        style={{
          "fill": cell.children ? colorScale(cell.name) : "none",
          "stroke": "white",
          "strokeWidth": "1.5px"
        }}/>
      <text
        x={3}
        y={12}
          style={{
            "fontFamily": "Helvetica",
            "fontSize": "10px",
            "border": "1px solid white",
            "fontWeight": cell.children ? "700" : "300"
          }}
          textAnchor={cell.hasChildren ? "end" : "start"}>
        {cell.value > hideLabelForValuesLessThan ? cell.name : ""}
      </text>
    </g>
  );
}
